/// <reference types="react" />
export default interface Stylable {
    /**
     * style - styles of the element.
     * @type object
     */
    style?: any;
    /**
     * className - css class of the element.
     * @type string
     */
    className?: string;
    /**
     * classes - classes, applied to the element.
     * @type object
     */
    classes?: any;
}