/**
 * copyToClipboard - function copies str value to client clipboard.
 * @function
 * @async
 * @param {any} str
 * @example
 * copyToClipboard("My string value is copied to user clipboard!");
 */
export default function copyToClipboard(str: any): Promise<void>;
